
public class IncDncOpr {

	public static void main(String[] args) 
	{
		int num = 5;
		++num;
		System.out.println(num);
		int num1 = 8;
		num1++;
		System.out.println(num1);
		int num3 = 9;
		--num;
		System.out.println(num3);
		int num4 = 5;
		num4--;
		System.out.println(num4);
		
		
		// TODO Auto-generated method stub

	}

}
