
public class LogicalOperator 
{

	public static void main(String[] args) 
	{
		int a = 20 ,b = 30;
		int x= 10, y = 15;
		System.out.println("(a<b && x<y) : "+(a<b && x<y));
		System.out.println("(a<b || x<y) : "+(a<b || x<y));
		System.out.println("(!(a<b && x<y)) : "+(!(a<b && x<y)));
		
		// TODO Auto-generated method stub

	}

}
